mapper.select:
SELECT count(1) AS cnt FROM ( SELECT entry_no FROM ( SELECT entry_no, state, entry_type, payment_type, name_keiyaku, name_keiyaku_k, shiki_date, kasai_hkikan, jishin_hkikan, kasai_hoho, jishin_hoho, dairi_cd, uke_kbn, katen_cd, del_flg, reject_flg, upd_date, ROW_NUMBER() OVER(PARTITION BY entry_no ORDER BY serial DESC) AS serialRownum FROM ENTRY_HA_TEMP
)
)

SELECT no , entry_no , policy , state , reject_flg , entry_type , payment_type , name_keiyaku , shiki_date , kasai_hoho , jishin_hoho , dairi_cd , dairi_name , uke_kbn , katen_cd , katen_name , del_flg , tate_keitai , shozai , kasai_hkikan , jishin_hkikan , sentaku_plan , o_hryo , f_hryo , upd_date FROM ( SELECT ROW_NUMBER() OVER(ORDER BY upd_date DESC, shiki_date DESC, entry_no) AS no , entry_no , policy , state , reject_flg , entry_type , payment_type , name_keiyaku , name_keiyaku_k , shiki_date , kasai_hoho , jishin_hoho , dairi_cd , dairi_name , uke_kbn , katen_cd , katen_name , del_flg , tate_keitai , shozai , kasai_hkikan , jishin_hkikan , sentaku_plan , o_hryo , f_hryo , upd_date , serialRownum FROM ( SELECT entry_no , policy , state , reject_flg , entry_type , payment_type , name_keiyaku , name_keiyaku_k , shiki_date , kasai_hoho , jishin_hoho , dairi_cd , dairi_name , uke_kbn , katen_cd , katen_name , del_flg , tate_keitai , shozai , kasai_hkikan , jishin_hkikan , sentaku_plan , o_hryo , f_hryo , ROW_NUMBER() OVER (PARTITION BY entry_no ORDER BY serial DESC) AS serialRownum , upd_date FROM ENTRY_HA_TEMP
)
) WHERE 1 = 1
AND no >= #{rownoStart}
AND no <= #{rownoEnd}

